SELECT m.name, d.dim_name_1,d.dim_value_1,d.dim_name_2,split_part(d.dim_value_2, ' ', 1) AS YEAR, split_part(d.dim_value_2, ' ', 2) AS QTR,d.dim_name_3,d.dim_value_3, d.dim_name_4,d.dim_value_4, r1.value as hospital_value, r2.value as phis_median, split_part(split_part(r1.result_name,'|||',2),'=',2) as unit FROM result r1 join dimension_set d on r1.set_id = d.set_id join measure m on r1.measure_id = m.measure_id join result r2 on r1.measure_id=r2.measure_id and r1.set_id = r2.set_id where m.name Like 'Surgeon entered for <=98%' AND split_part(split_part(r1.result_name,'|||',1),'=',2) ='HOSPITAL VALUE' AND split_part(split_part(r2.result_name,'|||',1),'=',2) ='PHIS MEDIAN'